Step.Script:
CREATE PROCEDURE "${Company}_SYS_SP_GET_USER_MODULES" (
    IN platform nvarchar(36),           --平台代码
    IN userCode nvarchar(8)             --用户编码
)
LANGUAGE SQLSCRIPT AS
BEGIN
    DECLARE platform2 nvarchar(36);
    DECLARE v_count integer;

    IF :platform = 'DESKTOP' OR :platform = 'TABLET' THEN
        SELECT 'COMBINATION' INTO platform2 FROM dummy;
    ELSE
        SELECT :platform INTO platform2 FROM dummy;
    END IF;

    SELECT count(0) into v_count FROM "${Company}_SYS_USER" WHERE "Code" = :userCode AND "Super" = 'Y';
    IF :v_count = 1 THEN
        --超级用户全部权限
        SELECT A.*,'A' "AuthValue"
        FROM "${Company}_SYS_MODULE" A
        WHERE (A."PlatformId" = :platform OR A."PlatformId" = :platform2) AND A."Activated" = 'Y';
    ELSE
        --非超级用户，角色权限
        SELECT A.*,C."AuthValue"
        FROM "${Company}_SYS_MODULE" A INNER JOIN (
            SELECT "ModuleId", "AuthValue"
            FROM "${Company}_SYS_PRIVILEGE"
            WHERE ("PlatformId" = :platform OR "PlatformId" = :platform2) AND "Activated" = 'Y' AND IFNULL("Target",'') = ''
                AND "RoleCode" IN (
                    SELECT "OrgCode"
                    FROM "${Company}_SYS_USER"
                    WHERE "Code" = :userCode
                )
            UNION ALL
            SELECT "ModuleId", "AuthValue"
            FROM "${Company}_SYS_PRIVILEGE1"
            WHERE ("PlatformId" = :platform OR "PlatformId" = :platform2) AND "Activated" = 'Y' AND IFNULL("Target",'') = ''
                AND "RoleCode" IN (
                    SELECT "OrgCode"
                    FROM "${Company}_SYS_USER"
                    WHERE "Code" = :userCode
                ) AND "IdentityCode" IN (
                    SELECT "Identity"
                    FROM "${Company}_SYS_USRIDNTY"
                    WHERE "User" = :userCode
                )
        ) C ON A."ModuleId" = C."ModuleId"
        WHERE (A."PlatformId" = :platform OR A."PlatformId" = :platform2) AND A."Activated" = 'Y';
    END IF;
END;

CREATE PROCEDURE "${Company}_SYS_SP_GET_USER_PRIVILEGES" ( IN platform nvarchar(36), --平台代码 IN userCode nvarchar(8) --用户编码 ) LANGUAGE SQLSCRIPT AS BEGIN DECLARE platform2 nvarchar(36); DECLARE v_count integer;
IF :platform = 'DESKTOP' OR :platform = 'TABLET' THEN SELECT 'COMBINATION' INTO platform2 FROM dummy; ELSE SELECT :platform INTO platform2 FROM dummy; END IF;
SELECT count(0) into v_count FROM "${Company}_SYS_USER" WHERE "Code" = :userCode AND "Super" = 'Y'; IF :v_count = 1 THEN -- 超级用户全部权限 SELECT TOP 0 'M' "Source", "ModuleId" "Target", "AuthValue" "Value", 'N' "Automatic" FROM "${Company}_SYS_PRIVILEGE"; ELSE -- 非超级用户，身份权限 SELECT "Source", "Target", "Value", "Automatic" FROM ( -- 模块的权限设置 SELECT 'M' "Source", "ModuleId" "Target", "AuthValue" "Value", "RoleCode", "Automatic" FROM "${Company}_SYS_PRIVILEGE1" WHERE ("PlatformId" = :platform OR "PlatformId" = :platform2) AND "Activated" = 'Y' AND IFNULL("Target",'') = '' AND "RoleCode" IN ( SELECT "OrgCode" FROM "${Company}_SYS_USER" WHERE "Code" = :userCode ) AND "IdentityCode" IN ( SELECT "Identity" FROM "${Company}_SYS_USRIDNTY" WHERE "User" = :userCode ) UNION ALL -- 应用的权限设置 SELECT 'A' "Source", "Target" "Target", "AuthValue" "Value", "RoleCode", "Automatic" FROM "${Company}_SYS_PRIVILEGE1" WHERE ("PlatformId" = :platform OR "PlatformId" = :platform2) AND "Activated" = 'Y' AND IFNULL("Target",'') <> '' AND "RoleCode" IN ( SELECT "OrgCode" FROM "${Company}_SYS_USER" WHERE "Code" = :userCode ) AND "IdentityCode" IN ( SELECT "Identity" FROM "${Company}_SYS_USRIDNTY" WHERE "User" = :userCode ) ) T0 UNION ALL -- 非超级用户，角色权限 SELECT "Source", "Target", "Value", "Automatic" FROM ( -- 模块的权限设置 SELECT 'M' "Source", "ModuleId" "Target", "AuthValue" "Value", "RoleCode", "Automatic" FROM "${Company}_SYS_PRIVILEGE" WHERE ("PlatformId" = :platform OR "PlatformId" = :platform2) AND "Activated" = 'Y' AND IFNULL("Target",'') = '' AND "RoleCode" IN ( SELECT "OrgCode" FROM "${Company}_SYS_USER" WHERE "Code" = :userCode ) UNION ALL -- 应用的权限设置 SELECT 'A' "Source", "Target" "Target", "AuthValue" "Value", "RoleCode", "Automatic" FROM "${Company}_SYS_PRIVILEGE" WHERE ("PlatformId" = :platform OR "PlatformId" = :platform2) AND "Activated" = 'Y' AND IFNULL("Target",'') <> '' AND "RoleCode" IN ( SELECT "OrgCode" FROM "${Company}_SYS_USER" WHERE "Code" = :userCode ) ) T0; END IF; END;
